package com.myfirstjava.program;

public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number =9;
for(int i=1; i<=number; i++) {
	
	for(int j=1; j<=i ; j++) {
		for(int k=1; k<=j; k++) {
			System.out.print("*");
		}
	}
	System.out.println();
		
	
}
	}

}
